#!/bin/bash# Kunci dan IV (harus sesuai dengan yang digunakan untuk enkripsi)KEY="c4ca4238a0b923820dcc509a6f75849b"  # Ganti dengan kunci hexadecimalIV="ef795f0a17f9b280a3a3a12a752d9a3e"    # Ganti dengan IV hexadecimal# Lokasi file terenkripsiENCRYPTED_FILE="premi_encrypted.bin"# Dekripsi di memori dan jalankanopenssl enc -aes-256-cbc -d -in "$ENCRYPTED_FILE" -K "$KEY" -iv "$IV" | bash